package trialanderror;

public class plusincrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int c = a++ ;
		System.out.println((c++) + (a++));
		System.out.println((c++) + (a++));
		System.out.println((c++) + (a++));
		System.out.println((c++) + (a++));
	}

}
